package TD1;


public class exo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Exercice 1 du TD1
		// Quelles sont les valeurs de a, b, c et d à la fin de l'algorithme suivant ?

		// Déclaration de 4 variables de type entier
		int a;
		int b;
		int c;
		int d;
		
		// Différentes manipulations des variables a, b, c et d 
		a=34;
		b=19;
		c=50;
		a=15;
		d=b;
		a=d;
		
		// Affichage des valeurs des variables à la fin des maipulations
		System.out.println("a = "+a);
		System.out.println("b = "+b);
		System.out.println("c = "+c);
		System.out.println("d = "+d);
	}

}
