package TD2;

public class exo8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Exercice 8 du TD2
		// Exécuter l'algorithme suivant :
		
		// Déclaration de la variable de type entier
		int a;
		
		// Affectation d'une valeur à la variable a
		a=7;
		
		// Boucle tant que qui affiche la valeur de a puis lui soustrait 2 
		// tant qu'elle est positive
		while(a>0) {
			System.out.println(a);
			a-=2;
		}
	}

}
